import React, { useState } from "react";

const MakePostForm = () => {
    const [title, setTitle] = useState();
    const [desc, setDesc] = useState();
    const [price, setPrice] = useState();
    async function postSubmit(event) {
        try {
            const fetchResponse = fetch('https://strangers-things.herokuapp.com/api/COHORT-NAME/posts' , {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify({
                    post: {
                        title: title,
                        description: desc,
                        price: price,
                    }
                })
            })
        } catch (error) {

        }
    }

    return (
        <div>
        <h1 className="center">Make a new post</h1>
        <div className="flexBox makePost">
            <form>
                <label>Title:</label>
                <input type='text' value={title}></input>
                <br></br>
                <label>Description:</label>
                <input type='text' value={desc}></input>
                <br></br>
                <label>Price:</label>
                <input type='text' value={price}></input>
                <br></br>
                <input type='submit'></input>
            </form>
        </div>
        </div>
    )
}

export default MakePostForm;
